import styled from "styled-components";
import Colors from "../color/colors";
import CheckImage from "../../assets/ic-check.svg";
import { useEffect, useState } from "react";
import { OutlinedButton } from "../button/button";
import BUTTON_SIZE from "../button/button-size";

const BackgroundWrapper = styled.div`
  padding-top: 50px;
  width: 720px;
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 16px;
`;

const OptionItem = styled.div`
  width: 168px;
  height: 168px;
  border-radius: 8px;
  cursor: pointer;
  background-color: ${({ type, color }) => (type === "color" ? color : "none")};
  background-image: ${({ type, url }) =>
    type === "image" ? `url(${url})` : "none"};
  background-position: center;
  background-size: cover;
  background-repeat: no-repeat;
`;

const CircleButtonWrapper = styled.div`
  height: 100%;
  overflow: hidden;
  display: flex;
  justify-content: center;
  align-items: center;

  & > button {
    border-radius: 50%;
  }
`;

function BackgroundSelect({ type, selected, onSelect }) {
  const [imageUrls, setImageUrls] = useState([]);

  const colorOptions = [
    { color: Colors.beige(200) },
    { color: Colors.purple(200) },
    { color: Colors.blue(200) },
    { color: Colors.green(200) },
  ];

  /* useEffect(() => {
    if (type !== "image") return;

    const BackgroundImageUrls = async () => {
      try {
        const response = await fetch(
          "https://rolling-api.vercel.app/background-images"
        );
        const data = await response.json();
        setImageUrls(data.imageUrls || []);
      } catch (error) {
        console.error(error);
        setImageUrls([]);
      }
    };
    BackgroundImageUrls();
  }, [type]); */

  // 이미지 옵션 테스트 코드
  useEffect(() => {
    if (type !== "image") return;

    const testImages = [
      "data:image/png;base64,",
      "data:image/jpeg;base64,",
      "data:image/jpeg;base64,",
      "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRs13WYFHZ6atXysoIMKKTUF7eHv9A3-iWaVw&s",
    ];
    setImageUrls(testImages || []);
  }, [type]);

  const options =
    type === "color"
      ? colorOptions
      : imageUrls.map((url, index) => ({ label: `${index}`, url }));

  return (
    <BackgroundWrapper>
      {options.map((option, index) => (
        <OptionItem
          key={index}
          type={type}
          color={option.color}
          url={option.url}
          onClick={() => onSelect(index)}
        >
          {selected === index && (
            <CircleButtonWrapper>
              <OutlinedButton
                size={BUTTON_SIZE.extraSmall}
                icon={CheckImage}
                style={{ backgroundColor: Colors.gray(500), boxShadow: "none" }}
              />
            </CircleButtonWrapper>
          )}
        </OptionItem>
      ))}
    </BackgroundWrapper>
  );
}

export default BackgroundSelect;
